import React from 'react';
import {Card, Col} from 'antd';
import 'antd/dist/antd.css';
import Histories from '../Lists/Histories';
import DateRange from '../DatePicker/DatePicker';
//import Images from '../Image';




const tabListNoTitle = [
    {
      key: 'Water',
      tab: 'Water',
    },
    {
      key: 'Soil',
      tab: 'Soil',
    },
    // {
    //   key: 'project',
    //   tab: 'project',
    // },
  ];

const contentListNoTitle = {
    Water: <p>article content</p>,
    Soil: <p>app content</p>,
    //project: <p>project content</p>,
  };

class StatCard extends React.Component {

    state = {
      noTitleKey: 'app',
    };

    onTabChange = (key, type) => {
      //console.log(key, type);
      this.setState({ [type]: key });
    };

    render() {
      return (
        <div>
          <Card
            style={{ width: '100%' }}
            tabList={tabListNoTitle}
            activeTabKey={this.state.noTitleKey}
            tabBarExtraContent={<DateRange />}
            onTabChange={key => {
              this.onTabChange(key, 'noTitleKey');
            }}
          >
            {contentListNoTitle[this.state.noTitleKey]}
            <Col span={12} >
              <img alt="map1" src="https://www.google.com/url?sa=j&url=https%3A%2F%2Fwww.shutterstock.com%2Fsearch%2Fgood&uct=1568201157&usg=ZV4yvbkgFsdEtKXnfxIGj5FxHUM.&source=images&cd=vfe&ved=0CAIQjRxqFwoTCNjEhKHX3OUCFQAAAAAdAAAAABAD" sizes="(min-width: 600px) 200px, 50vw"></img>
            </Col>
            <Col span={4} offset={12}>
              <Histories />
            </Col>

          </Card>
        </div>
      );
    }
  }

export default StatCard;
