  card: {
    minWidth: 600,
    minHeight: 600
  },
  wi: {
    color: "#673ab7"
  },
  atmospheric: {
    fontSize: "28px",
    padding: "5px"
  },
  recommendation: {
    fontFamily: "Montserrat, sans-serif",
    padding: "20px 0px 10px 0px",
    fontSize: "26px",
    textAlign: "center"
  },
  buttons: {
    color: "black"
  },
  list: {
    width: 400
  },
  fullList: {
    width: "auto"
  },
  aboutImg: {
    padding: "30px 150px 0px 150px",
    width: "100px"
  },
  aboutText: {
    fontFamily: "Montserrat",
    padding: "30px"
  },
  container: {
    display: "flex",
    flexWrap: "wrap"
  },
  search: {
    marginTop: "100px"
  },
  error: {
    color: "red",
    padding: "10px"
  }
}));

const WeatherSearch = props => {
  const classes = useStyles();
  const [searchTerm, setSearchTerm] = useState("");
  let [isSearching, setSearching] = useState(false);
  const debouncedSearchTerm = useDebounce(searchTerm, 1000);
  const hasError = props.error ? true : false;

  useEffect(() => {
    if (debouncedSearchTerm) {
      props.setCity(debouncedSearchTerm);
      // eslint-disable-next-line react-hooks/exhaustive-deps
      setSearching((isSearching = false));
    }
  }, [debouncedSearchTerm]);

  return (
    <div className={classes.search}>
      <Grid container alignItems="flex-end">
        <Grid item xs={12} style={{ textAlign: "center" }}>
          <FormControl>
            <Input
              id="search-city"
              error={hasError}
              placeholder="Enter city name"
              onChange={e => {
                setSearching((isSearching = true));
                setSearchTerm(e.target.value);
              }}
              startAdornment={
                <InputAdornment position="start">
                  <Tooltip title="Optional: Enter a two-letter country code after the city name to make the search more precise. For example, London, GB.">
                    <Search />
                  </Tooltip>
                </InputAdornment>
              }
              endAdornment={
                isSearching && (
                  <InputAdornment position="end">
                    <CircularProgress size={20} />
                  </InputAdornment>
                )
              }
            />
            {props.error && (
              <Typography className={classes.error}>{props.error}</Typography>
            )}
          </FormControl>
        </Grid>
      </Grid>
    </div>
  );
};

