import React from "react";
import {
 G2,
 Chart,
 Geom,
 Axis,
 Tooltip,
 Coord,
 Label,
 Legend,
 View,
 Guide,
 Shape,
 Facet,
 Util
} from "bizcharts";
import soilData from "../data.json";

class SoilElements extends React.Component {
 render() {
   const data = soilData;
   const cols = {
     month: {
       range: [0, 1]
     }
   };
   return (
     <div>
       <Chart height={400} data={data} scale={cols} forceFit>
         <Legend />
         <Axis name="month" />
         <Axis
           name="temperature"
           label={{
             formatter: val => `${val}Â°C`
           }}
         />
         <Tooltip
           crosshairs={{
             type: "y"
           }}
         />
         <Geom
           type="line"
           position="month*temperature"
           size={2}
           color={"city"}
           shape={"smooth"}
         />
         <Geom
           type="point"
           position="month*temperature"
           size={4}
           shape={"circle"}
           color={"city"}
           style={{
             stroke: "#fff",
             lineWidth: 1
           }}
         />
       </Chart>
     </div>
   );
 }
}

export default SoilElements;
